.blogpost-container {

    padding-left: 0;
    padding-right: 0;
	margin-top: $sc-margin-lv6;

    .blogpost-heading-image{

        img {
            width: 100%;
            object-fit: cover;
        }
    }
	.blogpost-content-row{
		@extend .row;
	}
	.blog-recent-post{
		h4{
			font-size:12px;
			font-weight:700;
			color:#999;
			margin-bottom:$sc-margin-lv2;
		}
		a{
			font-family:Verdana, Geneva, Tahoma, sans-serif;
			font-size:18px;
			font-weight:300;
			color:$sc-color-primary;
			display:block;
			padding: $sc-padding-lv1 0;
			&:hover{color:pink;}
		}
	}
	.blogpost-heading {
		padding-bottom: 10px;
		margin-bottom: 3%;
		border-bottom: 1px solid #ccc;

		.blogpost-title {
			margin-top: 1rem;
			font-size: 40px;
			font-weight:800;
			text-align:left;
			
		}

		.blogpost-author-date{
			color: #666;
			text-align: left;
			font-size: 12px;
			margin-top: $sc-margin-lv1;
			font-weight: 300;
			font-family:Verdana, Geneva, Tahoma, sans-serif;
		}
	}

	.blogpost-content {
		margin-top: 1.5rem;
		margin-bottom: 4rem;
		color: $sc-neutral-shade-300;
		p{font-family:$sc-font-family2;font-size:18px;font-weight:300;color:#0e1b23;margin-bottom: 20px;}
		a{
		color:$sc-color-primary;
		&:hover{color:$sc-color-link;}
		}

		ul{
			li {
				list-style: disc inside;
				color: inherit;
				font-size: inherit;
				margin-left: 20px;
				margin-top: 5px;
				margin-bottom: 5px;
			}
		}
	}
    /*.blogpost-tags{
        text-align: center;
        text-transform: uppercase;
        font-size: small;

        li {
            color: #FFF;
            display: inline;
            margin-right: 7px;
            border: 1px solid #15607b;
            background: #15607b;
            padding: 5px;
            padding-left: 8px;
            padding-right: 8px;
            border-radius: 6px;
            font-family: Calibri Light;
        }
    }*/
}

.blogpost-button-container {
    @extend .info-card-button-container;
}

.blogpost-list-card {
    @extend .info-card;
    line-height: 40px;
}

.blogpost-form {    
    margin-left: 5%;
    margin-right: 5%;
    margin-bottom: 5%;
    width: auto;

    input {
        width: 88%;
    }
}

.blogpost-clear {
    clear: both;
}

// blogpost fields

.blogpost-form-title {
    @extend .summary-title;
}

.blogpost-todo-title {
    display: inline-block;
}

.blogpost-completed {
    text-decoration: line-through;
}

// %= sass_class_name  buttons

.blogpost-button-add {
    @extend .button-primary;
    @extend .button-medium;
}

.blogpost-button-edit {
    @extend .button-secondary;
    @extend .button-small;

    float: right;
    margin-right: $sc-margin-lv3;
    margin-top: $sc-margin-lv1;
}

.blogpost-button-delete {
    @extend .icon-button;
    @extend .icon-button-controls;
    float: right;
    border-radius: 1.5em;
}

.blogpost-delete-icon {
    @extend .fa;
    @extend %fa-remove;
    color: none;
    margin-left: -1px;
    font-size: $sc-font-size-s;
}

.blogpost-submit-button {
    @extend .button-primary;
    @extend .button-medium;
}
